mapper.update:
update tSheet_sub
set
sheetsubSS2 = #{sheetsubSS2}
where
sheetsubSS = #{sheetsubSS}
and
sheetID2 = #{sheetID2}

update tSheet_sub s
s.fromsite = #{fromsite}, s.tosite = #{tosite}, s.item = #{item}, s.Qty = #{Qty}, s.Rem = #{Rem}, s.Qtyup = #{Qtyup}, s.writeridx2 = #{writeridx2}
where s.sheetsubID = #{sheetsubID}

mapper.delete:
delete from tSheet_sub
where sheetID2 IN (
select sheetID
from tSheet
where carSubmitTel = #{carSubmitTel}
and date = #{date}
and chk1 = 0
)
and
writeridx2=#{writeridx2}

mapper.sql:
WITH r1 AS
         (
             SELECT carSubmitTel, `date`
             FROM tsheet
             WHERE sheetID = #{sheetID}
         ), r2 as (
    select sheetID
    from tsheet t
             inner join r1 on t.carSubmitTel = r1.carSubmitTel and t.date = r1.date
)
